-- CREATE SEQUENCE product_seq START WITH 1 INCREMENT BY 1;
-- CREATE SEQUENCE category_seq START WITH 1 INCREMENT BY 1;
--
-- create table if not exists products (
--     id bigint primary key default nextval('product_seq') ,
--     name varchar(255) unique not null ,
--     description varchar(2000) ,
--     price numeric(10,2) not null check ( price > 0 ),
--     photo varchar,
--     count int check ( count >= 0 ) not null ,
--     rating numeric(1, 2) default 0
-- );
--
-- create table if not exists categories (
--     id bigint primary key default nextval('category_seq') ,
--     name varchar(255) unique not null
-- );
--
-- create table if not exists product_category (
--     product_id bigint references products(id) on delete set null ,
--     category_id bigint references categories(id) on delete set null ,
--     primary key (product_id, category_id)
-- );

-- Create tables with identity columns
CREATE TABLE IF NOT EXISTS products (
                                        id bigint PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
                                        name varchar(255) UNIQUE NOT NULL,
                                        description text,
                                        price numeric(10,2) NOT NULL CHECK (price > 0),
                                        photo_link varchar,
                                        count int CHECK (count >= 0) NOT NULL,
                                        rating numeric(3,2) DEFAULT 0,
                                        reviews_count int CHECK (reviews_count > 0),
                                        CONSTRAINT check_rating CHECK (rating IS NULL OR rating >= 1)
);

CREATE TABLE IF NOT EXISTS categories (
                                          id bigint PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
                                          name varchar(255) UNIQUE NOT NULL
);

CREATE TABLE IF NOT EXISTS product_category (
                                                product_id bigint REFERENCES products(id) ON DELETE CASCADE,
                                                category_id bigint REFERENCES categories(id) ON DELETE CASCADE,
                                                PRIMARY KEY (product_id, category_id)
);

CREATE TABLE IF NOT EXISTS photos (
                                      id bigint PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
                                      product_id bigint REFERENCES products(id) ON DELETE CASCADE,
                                      photoLink varchar
);

